import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Todo {
  id: number;
  summary: string;
  description: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  todos: Todo[] = [];

  newTodo: Todo = {
    id: 0,
    summary: '',
    description: ''
  };

  constructor(private http: HttpClient) {
    this.loadTodos();
  }

  loadTodos() {
    this.http.get<Todo[]>('http://localhost:8080/api/todos')
      .subscribe(data => {
        this.todos = data;
      });
  }

  addTodo() {
    this.http.post<Todo>('http://localhost:8080/api/todos', this.newTodo)
      .subscribe(data => {
        this.todos.push(data);
      });
  }

  getTodo(id: number) {
    this.http.get<Todo>(`http://localhost:8080/api/todos/${id}`)
      .subscribe(data => {
        const index = this.todos.findIndex(todo => todo.id === data.id);
        if(index !== -1) {
          this.todos[index] = data; // Update the existing Todo
        } else {
          this.todos.push(data); // Add the fetched Todo if it doesn't exist
        }
      });
  }

  deleteTodo(id: number) {
    this.http.delete(`http://localhost:8080/api/todos/${id}`)
      .subscribe(() => {
        this.todos = this.todos.filter(todo => todo.id !== id);
      });
  }
}
<h1>Todos</h1>
<table>
    <tr>
        <th>Id</th>
        <th>Description</th>
        <th>Summary</th>
        <th></th>
    </tr>
    <tr *ngFor="let todo of todos">
        <td>{{todo.id}}</td>
        <td>{{todo.description}}</td>
        <td>{{todo.summary}}</td>
        <td>
            <button (click)="deleteTodo(todo.id)">Delete</button>
        </td>
    </tr>
</table>
<button (click)="loadTodos()">Refresh</button>
<h2>Add New Todo</h2>
<label for="desc">Description:</label><br>
<input [(ngModel)]="newTodo.description" type="text" id="desc" name="desc"><br>
<label for="sum">Summary:</label><br>
<input [(ngModel)]="newTodo.summary" type="text" id="sum" name="sum"><br>
<button (click)="addTodo()">Add</button>
