import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Todo {
  id: number;
  summary: string;
  description: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  todos: Todo[] = [];

  newTodo: Todo = {
    id: 0,
    summary: '',
    description: ''
  };

  constructor(private http: HttpClient) {
    this.loadTodos();
  }

  loadTodos() {
    this.http.get<Todo[]>('http://localhost:8080/api/todos')
      .subscribe(data => {
        this.todos = data;
      });
  }

  addTodo() {
    this.http.post<Todo>('http://localhost:8080/api/todos', this.newTodo)
      .subscribe(data => {
        this.todos.push(data);
      });
  }

  deleteTodo(id: number) {
    this.http.delete(`http://localhost:8080/api/todos/${id}`)
      .subscribe(() => {
        this.todos = this.todos.filter(todo => todo.id !== id);
      });
  }
}
<table>
  <tr *ngFor="let todo of todos">
    <td>{{todo.summary}}</td>
    <td>{{todo.description}}</td>
    <td><button (click)="deleteTodo(todo.id)">Delete</button></td>
  </tr>
</table>

<h3>Add Todo:</h3>
<label>Summary: <input [(ngModel)]="newTodo.summary"></label>
<label>Description: <input [(ngModel)]="newTodo.description"></label>
<button (click)="addTodo()">Add</button>

<button (click)="loadTodos()">Refresh Todos</button>
